:root {
    --gray-7: #C4B79C;
    --gray-9: #D7CEBC;
    --gray-10: #E0DACC;
    --turq-dark: #006e89;
    --white: #E8E3D9;
    --green: #733900;
}

[data-theme='dark'] {
    --color-brand-primary: var(--turq-dark);
    --color-background: var(--white);
    --color-background-accented: var(--gray-10);
    --color-background-highlighted: var(--blue);
    --color-background-highlighted-link: var(--blue-light);
    --color-background-info: var(--gray-9);
    --color-background-warning: var(--yellow-light);
    --color-background-alert: var(--pink-light);
    --color-border: var(--gray-7);
    --color-text: var(--gray-1);
    --color-text-subtle: var(--gray-4);
    --color-code-comment: var(--green);
}
@media (prefers-color-scheme: dark) {
    :root:not([data-theme='light']) {
        --color-brand-primary: var(--turq-dark);
        --color-background: var(--white);
        --color-background-accented: var(--gray-10);
        --color-background-highlighted: var(--blue);
        --color-background-highlighted-link: var(--blue-light);
        --color-background-info: var(--gray-9);
        --color-background-warning: var(--yellow-light);
        --color-background-alert: var(--pink-light);
        --color-border: var(--gray-7);
        --color-text: var(--gray-1);
        --color-text-subtle: var(--gray-4);
        --color-code-comment: var(--green);
    }
}
.go-Icon--accented {
    filter: brightness(0) invert(45%) sepia(94%) saturate(6735%) hue-rotate(176deg) brightness(94%) contrast(101%);
}

[data-theme='dark'] .go-Icon:not(.go-Icon--accented) {
    filter: none;
}
[data-theme='dark'] .go-Icon--accented {
    filter: brightness(0) invert(45%) sepia(94%) saturate(6735%) hue-rotate(176deg) brightness(94%) contrast(101%);
}
@media (prefers-color-scheme: dark) {
    :root:not([data-theme='light']) .go-Icon:not(.go-Icon--accented) {
        filter: none;
    }
